<template>
  <nav>
    <nav class="nav">
      <span class="search" @click.stop="openSearchModal">üîç</span>
      <router-link class="logo" :to="{ name: 'Home' }">SpaceZ</router-link>
      <span class="locInsert"> Ïû•ÏÜåÎì±Î°ù</span>
      <span v-show="!logined" class="login" @click.stop="openModal">
        Î°úÍ∑∏Ïù∏</span
      >
      <!-- <span v-show="logined" class="login" @click="openLogOut"> -->
      <span v-show="logined" class="login" @click="handle_toggle">
        <img
          class="mypage"
          :src="`http://localhost:8090/spaceZBE/resources/upload/img_0001.png`"
        />
      </span>
      <!-- <span v-if="loggedIn" class="login" @click.stop="openModal"></span> -->
      <br />
    </nav>
    <hr />
    <LogoutModal v-if="showLogoutModal" @close="closeModal" />
    <SearchModal v-if="showSearchModal" @close="closeSearchModal" />
    <Modal v-if="showModal" @close="closeModal" />
  </nav>
</template>

<script>
// import { useRouter } from "vue-router";
import SearchModal from "@/components/SearchModal.vue";
import Modal from "@/components/LoginModal.vue";
import { ref } from "vue";
import LogoutModal from "@/components/LogoutModal.vue";

export default {
  components: {
    Modal,
    SearchModal,
    LogoutModal,
  },
  setup() {
    // const router = useRouter();
    const logined = ref("");
    const showModal = ref(false);
    const showLogoutModal = ref(false);
    const showSearchModal = ref(false);

    const loginCheck = () => {
      if (localStorage.getItem("memberId")) {
        logined.value = true;
      } else if (!localStorage.getItem("memberId")) {
        logined.value = false;
      }
    };

    loginCheck();

    const openLogOut = () => {
      showLogoutModal.value = true;
    };

    const openSearchModal = () => {
      showSearchModal.value = !showSearchModal.value;
    };

    const closeSearchModal = () => {
      showSearchModal.value = false;
    };

    const openModal = () => {
      showModal.value = true;
    };

    const closeModal = () => {
      showModal.value = false;
      showLogoutModal.value = false;
    };

    return {
      showSearchModal,
      logined,
      loginCheck,
      openModal,
      showModal,
      closeModal,
      openSearchModal,
      closeSearchModal,
      openLogOut,
      showLogoutModal,
    };
  },
};
</script>

