// eslint-disable-next-line
<template>
  <div>
    <div class="picsAndIntro">
      <div class="form">
        <p class="lineIntro">{{ details.space.spaceName }}</p>
        <br />
        <p class="shortInto">{{ details.space.info }}</p>
        <div class="img">
          <img
            class="big"
            name="big"
            :src="`http://localhost:8090/spaceZBE/resources/upload/${currentImg}`"
            @click="changePic"
          />
          <!-- details.imgs[currentImgNum].img -->
          <br />

          <img
            class="list"
            v-for="name in details.imgs"
            :key="name.id"
            :src="name.img"
            @click="directChangePic"
          />
        </div>
      </div>
    </div>
    <div class="res">
      <div class="resForm">
        <div v-if="details.space.type == 'office'">
          <p class="chooseDate">날짜 선택</p>
          <div class="calender">
            <input
              class="date"
              type="date"
              v-model="startDate"
              style="margin-right: 20px"
            />
            <span style="font-size: 25px">-</span>
            <input
              class="date"
              type="date"
              v-model="endDate"
              style="margin-left: 20px"
              @change="rentOfficeCheck"
            />
          </div>
          <p>{{ errMessage }}</p>
          <div class="priceDiv">
            <p class="price">
              가격
              <!-- <input type="number" v-model="officePrice" /> -->
              {{ officePrice }}
              원
            </p>
          </div>
          <div class="mileageDiv">
            <p class="price" style="margin-left:20px;">마일리지사용</p>
            <input class="mileage" v-show="!useMileage" type="number" min="1" v-model="mileage"/>
            <span class="mileage" v-show="useMileage">{{mileage}}</span>
            <button v-show="!useMileage" class="mileageBtn" @click="changeMileageState">사용</button>
            <button v-show="useMileage" class="usedMileageBtn" @click="changeMileageState">취소</button>
          </div>
          <div>
            <p class="possibleMileage">사용가능한 마일리지: {{ details.total_score }}점</p>
          </div>
          <p class="payRule">
            &#8251;오피스 예약은 후불제이며 선결제가 불가능합니다&#8251;
          </p>
          <button @click="resSubmit">예약하기</button>
        </div>
        <div v-if="details.space.type !== 'office'">
          <p class="chooseDate">날짜 및 시간 선택</p>
          <div class="calender">
            <label style="font-size: 20px">대여시각: {{ now }}-</label>
            <select
              class="time"
              v-model="startDateTime"
              style="margin-right: 10px"
            >
              <option v-for="num1 in 24" :key="num1.id" :value="num1">
                {{ num1 }}시
              </option>
            </select>
            <label style="margin-left: 5px; font-size: 20px">반납시각:{{ now }}-</label>
            <select
              class="time"
              v-model="endDateTime"
              style="margin-right: 20px"
              @change="rentTimeCheck"
            >
              <option v-for="num2 in 24" :key="num2.id" :value="num2">
                {{ num2 }}시
              </option>
            </select>
          </div>
          <p>{{ errMessage }}</p>
          <div class="priceDiv">
            <p class="price">가격 {{ nOfficePrice }}원</p>
          </div>
          <div class="mileageDiv">
            <p class="price" style="margin-left:20px;">마일리지사용</p>
            <input class="mileage" v-show="!useMileage" type="number" min="1" v-model="mileage"/>
            <span class="mileage" v-show="useMileage">{{mileage}}</span>
            <button v-show="!useMileage" class="mileageBtn" @click="nchangeMileageState">사용</button>
            <button v-show="useMileage" class="usedMileageBtn" @click="nchangeMileageState">취소</button>
